import React from "react";
import { connect } from "react-redux";
import { getAllUsers } from "../../redux/action-creators";
import { getAllDetails } from "../../redux/action-creators";


class App extends React.Component {
  componentDidMount() {
    this.props.getAll();
  }

  render() {
    const { isLoading, err, list } = this.props.userList;
    const { isLoading: detailLoading, err: detailError, details} = this.props.userDetail;
    return (
      <div className = 'container'>
        {isLoading && <h1>Loading...</h1>}
        {!isLoading &&
          <div className = 'userList'>
            {err && <h1>Error Occured!</h1>}
            <h1>List</h1>
            {list.length > 0 &&
              <table>
                <thead>
                  <th>ID</th>
                  <th>username</th>
                  <th>image</th>
                </thead>
                <tbody>
                  {list.map((user) => {
                    return (
                      <tr>
                        <td>{user.id}</td>
                        <td>{user.login}</td>
                        <td>
                          <img src = {user.avatar_url} alt = {`${user.login} avatar>`}/>
                        </td> 
                      </tr>
                    );
                  })} 
                </tbody>
              </table>
            }
          </div>
        }
        <div className = 'userDetail'>   
        {detailLoading && <h1>Loading Detail</h1>}
        {!detailLoading && 
            <div>
            {detailError && <h1>Detail Loading Error</h1>}
            {details && <div>
              <h1>Detail</h1>
              <div>name:{details.name}</div>
              <div>location:{details.location}</div>
              <div>following:{details.following_url}</div>
              <div>followers:{details.followers_url}</div>
            </div>}
          </div>
        }
        </div>
      </div>
    );
  }
};

const mapStateToProps = (state) => {
  return {
    userList: state.userList,
    userDetail: state.userDetail,
  };
};
const mapDispatchToProps = (dispatch) => {
  return {
    getAll: () => {
      dispatch(getAllUsers());
      dispatch(getAllDetails());
    }
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(App);
