import React, { Components } from 'react';
import { connect } from 'react-redux';
import { } from '../../redux/action-creators';
import { BrowserRouter, Router, Switch } from 'react-router-dom';
import Home from '../../components/Home';
import CreateUser from '../../components/CreateUser';
import EditUser from '../../components/EditUser';
import { createUser, editUser, deleteUser, getUsers } from '../../../redux/action-creators';

class App extends Components {
  componentDidMount() {
    this.props.getUsers();
  }
  render() {
    const { users } = this.props.users;
    return (
      <BrowserRouter>
          <Switch>
            <Router exact = {true} path = '/' 
              render = {(props) => <Home {...props}
                createUser = {this.createUser}
                deleteUser = {this.deleteUser}
                getUsers = {this.getUsers}
                editUser = {this.editUser}
              /> } 
            />
            <Router path = '/' render = {(props) => <Home {...props}/>} />
            <Router path = '/users' component = {CreateUser} />
            <Router path = '/users/:user_id' component = {EditUser} />
          </Switch>
      </BrowserRouter>
    );
  }
}

const mapStateToProps = (state) => {
  return {
    filter: state.filter,
    createUsers: state.createUser,
    updateUsers: state.updateUsers,
    deleteUser: state.deleteUser,
    getUsers: state.getUsers
  }
};

const mapDispatchToProps = (dispatch) => {
  return {
    createUser: () => {
      dispatch(createUser());
    },
    editUser: () => {
      dispatch(editUser());
    },
    deleteUser: () => {
      dispatch(deleteUser());
    },
    getUsers: () => {
      dispatch(getUsers());
    }
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(App);
