import React, { Component } from './node_modules/react';
import { connect } from './node_modules/react-redux';
import { createUser, editUser, deleteUser, getUsers } from '../../redux/action-creators';
import { BrowserRouter, Router } from 'react-router-dom';
import { withRouter } from 'react-router-dom';

class Home extends React.Component {
  constructor(props) {
    super(props);
    this.state = { users, firstname, lastname, sex, age, password, searchText: ''};
    this.routeChange = this.routeChange.bind(this);
  }

  routeEdit() {
    let path = `http://localhost:8080/api/users`;
    this.props.history.push(path);
  }

  routeCreate() {
    let path = `http://localhost:8080/api/users`;
    this.props.history.push(path);
  }

  render() {
    const { users } = this.state;
    return (
      <div className = 'container'>
        <h2>Users</h2>
        <div className = 'searchBox'>
          <form>
            Search:
            <input type = 'text' name = 'search' />
          </form>
        </div>
        <table>
          <thead> 
            <tr>
              <th>Edit</th>
              <th>Delete</th>
              <th>First Name</th>
              <th>Last Name</th>
              <th>Sex</th>
              <th>Age</th>
            </tr>    
          </thead>
          <tbody>
            {users.map((user) => {
              return (
                <tr>
                  <td><button onClick={this.routeEdit}>Edit</button></td>
                  <td><button onClick={this.routeDelete}>Delete</button></td>
                  <td>{user.firstname}</td>
                  <td>{user.lastname}</td>
                  <td>{user.sex}</td>
                  <td>{user.age}</td>
                </tr>
              );
            })} 
          </tbody>
        </table>
        <button onClick={this.handleCreateClick}>Create New User</button> 
      </div>
    );
  }
}

export default withRouter(Home);
