import React, {Component}from "react";

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {usernameInput: '', passwordInput: ''};

  }
  handleUsernameInputChange = e => {
    this.setState({usernameInput: e.target.value});
    //this.setState({passwordInput: e.target.value});
    console.log('username input: ', e.target.value);
  }

  handlePasswordInputChange = e => {
    this.setState({passwordInput: e.target.value});
    console.log('password input: ', e.target.value);
  }


  handleSubmit = e => {
    e.preventDefault();
    console.log('the text in the form are: ', this.state);
  }

  render () {
    return (
      <div>
        <form className = 'inputForm' onSubmit = {this.handleSubmit}>
          <div>Username</div>
          <input type = 'text' value = {this.state.usernameInput} onChange = {this.handleUsernameInputChange()} />
          <div>Password</div>
          <input type = 'text' value = {this.state.passwordInput} onChange = {this.handlePasswordInputChange()} />
          <div>
            <input type = 'submit' value = 'Submit' />
          </div>
        </form>
      </div>
    );
  }
}

export default App;
