import React from "react";
import { connect } from "react-redux";
import { getAllUsers } from "../../redux/action-creators";
import { getAllDetails } from "../../redux/action-creators";


class App extends React.Component {
  componentDidMount() {
    this.props.getAll();
  }

  render() {
    const { isLoading, err, list } = this.props.userList;
    const { isLoading: detailLoading, err: detailError, details} = this.props.userDetail;
    const { getDetail } = this.props;
    return (
      <div className = 'container'>
        {isLoading && <h1>Loading...</h1>}
        {!isLoading &&
          <div className = 'userList'>
            {err && <h1>Error Occured!</h1>}
            {list.length > 0 &&
              <div>
                <h1>List</h1>
                <table>
                  <thead>
                    <th>ID</th>
                    <th>username</th>
                    <th>image</th>
                  </thead>
                  <tbody>
                    {list.map((user) => {
                      return (
                        <tr key = {user.id} onClick = {() => getDetail(user.login)}>
                          <td>{user.id}</td>
                          <td>{user.login}</td>
                          <td>
                            <img src = {user.avatar_url} alt = {`${user.login} avatar`}/>
                          </td> 
                        </tr>
                      );
                    })} 
                  </tbody>
                </table>
              </div>
            }
          </div>
        }
        <div>   
          {detailLoading && <h1>Loading Detail</h1>}
          {!detailLoading && 
            <div>
              <h1>Detail</h1>
              {detailError && <h1>Detail Loading Error</h1>}
              {details.name && 
                <div className = 'userDetail'>
                  <p className = 'detail'> name:{details.name}</p>
                  <p className = 'detail'> location:{details.location}</p>
                  <p className = 'detail'> following:{details.following}</p>
                  <p className = 'detail'> followers:{details.followers}</p>
                </div>
              }
            </div>
          }
        </div>
      </div>
    );
  }
}

const mapStateToProps = (state) => {
  return {
    userList: state.userList,
    userDetail: state.userDetail
  };
};
const mapDispatchToProps = (dispatch) => {
  return {
    getAll: () => {
      dispatch(getAllUsers());
    },
    getDetail: (login) => {
      dispatch(getAllDetails(login));
    }
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(App);
