const initState = {
  isLoading: false,
  //users:[],
  err: null,
  isSearching: false,
  searchUsers: [],

  curPage: 1,
  count: 0,
  pageSize: 5,
  startIndex: 0,
  pageOfUsers: [],
  pageNumber: 0,
};

const getUsers = (state = initState, action) => {
  switch(action.type) {
    // Get UserList 
    case 'GET_USERS_START':
      return {
        ...state,
        isLoading: true,
        err: null
      };
    case 'GET_USERS_FAIL':
      return {
        ...state,
        isLoading: false,
        err: action.error
      };
    case 'GET_USERS_SUCCESS':
      return {
        ...state,
        isLoading: false,
        err: null,
        users: action.data
      };

    // Delete User
    case 'DELETE_USER_START':
      return {
        ...state,
        isLoading: true
      };
    case 'DELETE_USER_ERROR':
      return {
        ...state,
        isLoading: false,
        err: action.error
      };
    case 'DELETE_USER_SUCCESS':
      let index = state.pageOfUsers.findIndex((item) => item._id === action.id);

      return {
        ...state,
        pageOfUsers: [
          ...state.pageOfUsers.slice(0, index),
          ...state.pageOfUsers.slice(index + 1)
        ],
       // users: state.users.filter(user => user._id !== action.id),
        isLoading:false,
        err: null,
      };

    // Create New User 
    case 'CREATE_USER_START':
      return {
        ...state,
        isLoading: true
      };
    case 'CREATE_USER_FAIL':
      return {
        ...state,
        isLoading: false,
        err: action.error
      };
    case 'CREATE_USER_SUCCESS':
      // const newUsers = state.users;
      // newUsers.push(action.user);
      if (state.pageOfUsers.length < state.pageSize) {
        return {
          ...state,
          isLoading: false,
          pageOfUsers:[
            ...state.pageOfUsers,
            action.user,
          ]
        };
      } else {
        return state;
      }


    // Edit User 
    case 'EDIT_USER_START':
      return {
        ...state,
        isLoading: true
      };
    case 'EDIT_USER_ERROR':
      return {
        ...state,
        isLoading: false,
        err: action.error
      };
    case 'EDIT_USER_SUCCESS':
      let newUser = state.pageOfUsers.map(user => {
        if (user._id === action.id) {
          return action.user;
        } else {
          return user;
        }
      });
      return {
        ...state,
        isLoading: false,
        // err: null,
        pageNumber: newUser
      };

    // Sort Users
    case 'SORT_USERS': {
      const sortedUsers = [...state.users]; //user -- page of users pagination之后需要改
      const key = action.key;
      sortedUsers.sort((user1, user2) => {
        if (typeof(user1[key]) === 'number') {
          if (user1[key] === user2[key]) {
            return 0;
          }
          return user1[key] < user2[key] ? -1 : 1;
        }
        return user1[key].toLowerCase().localeCompare(user2[key].toLowerCase());
      });
      //做完search之后需要加的
      // const newSearch = [...state.searchUsers]; 
      // sortedUsers.sort((user1, user2) => {
      //   if (user1[key] === user2[key]) {
      //     return 0;
      //   }
      //   return user1[key] < user2[key] ? -1 : 1;
      // });
      return {
        ...state,
        users: sortedUsers,
        //searchUsers : newSearch
      };
    }

    case 'SEARCH_START' : {
      return {
        ...state,
        isLoading: true,
      }
    }
    case 'SEARCH_SUCCESS' : {
      return {
          ...state,
          searchUsers : action.users,
          isSearching : true,
          isLoading: false,
      }
    }
    case 'SEARCH_FAIL' : {
      return {
        ...state,
        isLoading: false,
        isSearching: false,
        err: action.error
      }
    }
    case 'BACK_TO_HOME' : {

      return{
        ...state,
        searchedUser : [],
        isSearching : false,
      }

    }

    default:
      return state;
  }
}

export default getUsers;


