import axios from 'axios';

const getUserStart = () => {
  return {
    type: 'GET_USER_START'
  };  
};
const getUserFail = (error) => {
  return {
    type:'GET_USER_FAIL',
    error
  };
};

const getUserSuccess = (response) => {
  return {
    type:'GET_USER_SUCCESS',
    data: response
  }
};

const getDetailStart = () => {
  return {
    type: 'GET_DETAIL_START'
  };
};

const getDetailFail = (error) => {
  return {
    type: 'GET_DETAIL_ERROR',
  };
};

const getDetailSuccess = (response) => {
  return {
    type: 'GET_DETAIL_SUCCESS',
  };
}

export const getAllUsers = () => {
  return (dispatch) => {
    dispatch(getUserStart());
    axios.get('https://api.github.com/users?per_page=100')
      .then(response => {
        dispatch(getUserSuccess(response.data));
      })
      .catch(error => {
        dispatch(getUserFail(error));
      });
  };
};

export const getAllDetails = (id) => {
  return (dispatch) => {
    dispatch(getDetailStart());
    axios.get(`https://api.github.com/users/${id}`)
      .then(response => {
        dispatch(getDetailSuccess(response.data));
      })
      .catch(error => {
        dispatch(getDetailFail(error));
      });
  };
};
