import React, { Component } from 'react';
import { connect } from 'react-redux';
import { getUsers, deleteUser, sortUsers } from '../../redux/action-creators';
import { Link } from 'react-router-dom';

class UserList extends Component {
  componentDidMount() {
    this.props.dispatch(getUsers());
    this.props.resetRedirect();
  }

  deleteUser = id => {
    this.props.dispatch(deleteUser(id));
  }

  handleSort = (e, key) => {
    e.preventDefault();
    this.props.dispatch(sortUsers(key));
  }

  render() {
    //return (
    if (this.props.isLoading) {
      return <div>Loading...</div>
    } else {
      return (
        <div className = 'container'>
          <h2>Users List</h2>

          <div className = 'searchBox'>  
            <form>
              Search:
              <input type = 'text' name = 'search' />
            </form>
          </div>

          <table>
            <thead> 
              <tr>
                <th>Edit</th>
                <th>Delete</th>
                <th>
                  <button className = "btn btn-light" onClick = {e => this.props.handleSort(e, users.firstname)}>
                    First Name
                  </button>
                </th>
                <th>
                  <button className = "btn btn-light" onClick = {e => this.props.handleSort(e, 'lastname')}>
                    Last Name
                  </button>
                </th>
                <th>
                  <button className = "btn btn-light" onClick = {e => this.props.handleSort(e, 'sex')}>
                    Sex
                  </button>
                </th>
                <th>
                  <button className = "btn btn-light" onClick = {e => this.props.handleSort(e, 'age')}>
                    Age
                  </button>
                </th>
              </tr>    
            </thead>

            <tbody>
              {this.props.users.map(user => {
                return (
                  <tr key = {user._id}>
                    <td><Link to={`/users/${user._id}`} className="btn btn-primary">Edit</Link></td>
                    <td><button type = 'button' onClick={() => this.props.deleteUser(user._id)}>Delete</button></td>
                    <td>{user.firstname}</td>
                    <td>{user.lastname}</td>
                    <td>{user.sex}</td>
                    <td>{user.age}</td>
                  </tr>
                );
              })} 
            </tbody> 
          </table>
          <Link to="/users" className="btn btn-primary" > 
            Create New User
          </Link>
        </div>
      );
    }
  }
}

const mapStateToProps = (state) => {
  return {
    users: state.getUsers.users
  };
}


export default connect(mapStateToProps)(UserList);
