import React, {Component}from "react";
import moment from 'moment';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {usernameInput: '', passwordInput: '', isLogging: false};
  }
  handleUsernameInputChange = e => {
    this.setState({usernameInput: e.target.value});
    console.log('username input: ', e.target.value);
  }

  handlePasswordInputChange = e => {
    this.setState({passwordInput: e.target.value});
    console.log('password input: ', e.target.value);
  }


  handleSubmit = e => {
    e.preventDefault();
    console.log('the text in the form are: ', this.state);

    if ((this.state.usernameInput === 'today' && this.state.passwordInput === moment().format('YYYYMMDD')) || 
        (this.state.usernameInput === 'yesterday' && this.state.passwordInput === moment().add(-1, 'days').format('YYYYMMDD')) ||
        (this.state.usernameInput === 'tomorrow' && this.state.passwordInput === moment().add(1, 'days').format('YYYYMMDD'))) {
          this.setState({isLogging: true});
    } else {
        this.setState({isLogging: false});
    } 
  }

  render () {
    const { usernameInput, passwordInput, isLogging } = this.state;
    return (
      <div>
        <form className = 'inputForm' onSubmit = {this.handleSubmit}>
          <div>Username</div>
          <input type = 'text' value = {usernameInput} onChange = {this.handleUsernameInputChange} />
          <div>Password</div>
          <input type = 'text' value = {passwordInput} onChange = {this.handlePasswordInputChange} />
          <div>
            <input type = 'submit' value = 'Submit' />
            <h1>{isLogging && 'welcome back'}</h1>
            <h1>{!isLogging && 'login failed'}</h1>
          </div>

        </form>
      </div>
    );

  }
}

export default App;
