const initState = {
  isLoading: false,
  users:[],
  err: null
};

const getUsers = (state = initState, action) => {
  switch(action.type) {
    case 'GET_USER_START':
      return {
        ...state,
        isLoading: true,
        err: null
      };
    case 'GET_USER_FAIL':
      return {
        ...state,
        isLoading: false,
        err: action.error
      };
    case 'GET_USER_SUCCESS':
      return {
        ...state,
        isLoading: false,
        err: null,
        users: action.data
      };

    case 'DELETE_USER_START':
      return {
        ...state,
        isLoading: true
      };
    case 'DELETE_USER_ERROR':
      return {
        ...state,
        isLoading: false,
        err: action.error
      };
    case 'DELETE_USER_SUCCESS':
      const newUsers = state.users.filter(user => user.id !== action.id);
      return {
       // ...state.users.filter(user => user.id !== action.id),
        ...state,
        users: newUsers,
        isLoading:false,
        err: null,
      };
    default:
      return state;
  }
}

export default getUsers;
