import axios from 'axios';

const createUserStart = () => {
  return {
    type: 'CREATE_USER_START'
  };  
};
const createUserFail = (error) => {
  return {
    type:'CREATE_USER_FAIL',
    error
  };
};

const createUserSuccess = (response) => {
  return {
    type:'CREATE_USER_SUCCESS',
    data: response
  }
};

const updateUserStart = () => {
  return {
    type: 'UPDATE_USER_START'
  };
};

const updateUserFail = (error) => {
  return {
    type: 'UPDATE_USER_ERROR',
    error
  };
};

const updateUserSuccess = (response) => {
  return {
    type: 'UPDATE_USER_SUCCESS',
    data: response
  };
}

const deleteUserStart = () => {
  return {
    type: 'DELETE_USER_START'
  };
};

const deleteUserFail = (error) => {
  return {
    type: 'DELETE_USER_ERROR',
    error
  };
};

const deleteUserSuccess = (response) => {
  return {
    type: 'DELETE_USER_SUCCESS',
    data: response
  };
}


export const createUsers = () => {
  return (dispatch) => {
    dispatch(createUserStart());
    axios.put('https://localhost:8080/api/users')
      .then(response => {
        dispatch(createUserSuccess(response.data));
      })
      .catch(error => {
        dispatch(createUserFail(error));
      });
  };
};



export const updateUser = (id, updateUser) => {
  return (dispatch) => {
    dispatch(updateUserStart());
    axios.put(`https://localhost:8080/api/users/${id}`, updateUser)
      .then(response => {
        dispatch(updateUserSuccess(response.data));
      })
      .catch(error => {
        dispatch(updateUserFail(error));
      });
  };
};

export const deleteUser = (id, deleteUser) => {
  return (dispatch) => {
    dispatch(deleteUserStart());
    axios.delete(`https://localhost:8080/api/users/${id}`, deleteUser)
      .then(response => {
        dispatch(deleteUserSuccess(response.data));
      })
      .catch(error => {
        dispatch(deleteUserFail(error));
      });
  };
};
