import React from "react";
import { connect } from "react-redux";
import { addOne } from "../../redux/action-creators"
class App extends React.Component {
  constructor(props) {
    super(props);
  }
  handleAdd = () => {
    this.props.dispatch();
  }
  render() {
    return (
      <div>
        <div>{this.props.number}</div>
        <button onClick ={this.handleAdd}>+</button>
      </div>
    )
  }
}
const mapStateToProps = state => ({  //这个state是redux state
  state:state  //前面的state是react， 后面的是redux的state
});

export default connect(mapStateToProps)(App);
