import React, { Component } from 'react';
//import { connect } from 'react-redux';
//import { createUser }  from '../../redux/action-creators';
//import { create } from 'domain';
import { withRouter } from 'react-router-dom';

class CreateUser extends Component {
  constructor(props) {
    super(props);
    this.state = { input: '' };
  }

  routeAdd() {
    let path = `http://localhost:8080/api/`;
    this.props.history.push(path);
  }

  handleInput = e => {
    this.setState({input: e.target.value});
  }
  handleSubmit = e => {
    e.preventDefault();
    this.props.createUser(this.state.input);
    this.setState({input: ''});
  }

  render() {
    return (
      <div className = 'container'>
        <h2>Crete New User</h2>
        <form onSubmit = {this.handleSubmit}>
          First Name: <input type = 'text' name = 'firstname' value = {this.state.input} onChange = {this.handleInput} />
          Last Name: <input type = 'text' name = 'lastname' value = {this.state.input} onChange = {this.handleInput} />
          Sex: <input type = 'text' name = 'sex' value = {this.state.input} onChange = {this.handleInput} />
          Age: <input type = 'text' name = 'age' value = {this.state.input} onChange = {this.handleInput} />
          Password: <input type = 'text' name = 'password' value = {this.state.input} onChange = {this.handleInput} />
          Password Repeat: <input type = 'text' name = 'password'/>
        </form>
        <button type = "submit" onClick={this.routeAdd}>Add User</button> 
      </div>
    );
  }
}


export default withRouter(CreateUser);
