const initState = {
  details: {},
  err: null,
  isLoading: false
};

const userDetail = (state = initState, action) => {
  switch(action.type) {
    case 'GET_DETAIL_START':
      return {
        ...state,
        isLoading: true
      };
    case 'GET_DETAIL_ERROR':
      return {
        ...state,
        isLoading: false,
        err: action.error
      };
    case 'GET_DETAIL_SUCCESS':
      return {
        ...state,
        isLoading:false,
        err: null,
        details: Response.data
      };
    default:
      return state;
  }
}

export default userDetail;
