import React, {Component}from "react";

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {textInput: ''};
  }
  handleInputChange = e => {
    this.setState({textInput: e.target.value});
    console.log('username or password input: ', e.target.value);
  }

  handleSubmit = e => {
    e.preventDefault();
    console.log('the text in the form are: ', this.state);
  }

  render () {
    return (
      <div>
        <form className = 'inputForm' onSubmit = {this.handleSubmit}>
          Username
          <input type = 'text' value = {this.state.textInput} onChange = {this.handleInputChange} />
          Password
          <input type = 'text' value = {this.state.textInput} onChange = {this.handleInputChange} />
        </form>
      </div>
    );
  }
}

export default App;
