import React, { Component } from 'react';
import { connect } from 'react-redux';
import { getUsers } from '../../redux/action-creators';
//import { BrowserRouter, Router } from 'react-router-dom';
//import { withRouter } from 'react-router-dom';

class Home extends Component {
  constructor(props) {
    super(props);
    this.state = { 
      firstname: "default", 
      lastname: "default",
      sex: "default", 
      age: "default"
    };
    //this.routeChange = this.routeChange.bind(this);
  }

  /*routeEdit() {
    let path = `http://localhost:8080/api/users/:user.id`;
    this.props.history.push(path);
  }

  routeCreate() {
    let path = `http://localhost:8080/api/users`;
    this.props.history.push(path);
  }
*/

  render() {
    const { users } = this.state;
    return (
      <div className = 'container'>
        <h2>Users</h2>
        <div className = 'searchBox'>
          <form>
            Search:
            <input type = 'text' name = 'search' />
          </form>
        </div>
        <table>
          <thead> 
            <tr>
              <th>Edit</th>
              <th>Delete</th>
              <th>First Name</th>
              <th>Last Name</th>
              <th>Sex</th>
              <th>Age</th>
            </tr>    
          </thead>
          <tbody>
            {users.map((user) => {
              return (
                <tr>
                  <td><button>Edit</button></td>
                  <td><button>Delete</button></td>
                  <td>{user.firstname}</td>
                  <td>{user.lastname}</td>
                  <td>{user.sex}</td>
                  <td>{user.age}</td>
                </tr>
              );
            })} 
          </tbody>
        </table>
        <button onClick={this.routeCreate}>Create New User</button> 
      </div>
    );
  }
}

const mapStateToProps = (state) => {
  return {
    users: state.getUsers.data
  }
};

const mapDispatchToProps = (dispatch) => {
  return {
    getUsers: () => {
      dispatch(getUsers());
    },
  }
};

export default connect((mapStateToProps, mapDispatchToProps),Home);
