import React, { Component } from 'react';
import { connect } from "react-redux";
import { deleteUser, createUser, resetRedirect, redirect, editUser, sortUsers, backToHome, search, changeSearchInput, getCount,getUsers} from '../../redux/action-creators';
import { BrowserRouter, Route, Switch } from 'react-router-dom';
import UserList from '../../components/UserList';
import CreateUser from '../../components/CreateUser';
import EditUser from '../../components/EditUser';

class App extends Component { 
  getUser = (_id) => {
    console.log('get edit user is called');
    const users = this.props.pageOfUsers;
    for (let user of users) {
      if (user._id === _id) {
        return user;
      }
    }
  };

  render() {
    return (
      <BrowserRouter>
        <Switch>
          <Route
            exact = {true} 
            path = '/' 
            render = {() => 
              <UserList 
                pageOfUsers = {this.props.pageOfUsers}
                getUsers = {this.props.getUsers}
                deleteUser = {this.props.deleteUser}
                sortUsers = {this.props.sortUsers}
                isLoading = {this.props.isLoading}
                resetRedirect = {this.props.resetRedirect}
                search = {this.props.search}
                isSearching = {this.props.isSearching}
                searchUsers = {this.props.searchUsers}
                searchChange = {this.props.changeSearchInput}
                searchInput = {this.props.searchInput}
                backToHome = {this.props.backToHome}
                getCount = {this.props.getCount}
                curPage = {this.props.curPage}
                users = {this.props.users}
              /> 
            } 
          />
          <Route 
            exact = {true}
            path = '/users' 
            render = {() => 
              <CreateUser 
                createUser = {this.props.createUser} 
                redirect = {this.props.redirect}
                redirectToUserlist = {this.props.redirectToUserlist}
                isLoading = {this.props.isLoading}
              />
            }
          />
           <Route 
            path = '/users/:user_id' 
            render = {({ match }) => {
              return (
                <EditUser
                  users = {this.props.users}
                  id = {match.params.user_id}
                  editUser = {this.props.editUser}
                  getUsers = {this.props.getUsers}
                  getUser = {this.getUser}
                  redirect = {this.props.redirect}
                  redirectToUserlist = {this.props.redirectToUserlist}
                  isLoading = {this.props.isLoading}
                /> 
              );
            }}
          />
        </Switch>
      </BrowserRouter>
    );
  }
}

const mapStateToProps = (state) => {
  return {
    redirect: state.redirect,
    isLoading: state.getUsers.isLoading,
    isSearching: state.searchInput.isSearching,
    searchUsers: state.getUsers.searchUsers,
    searchInput: state.searchInput.searchInput,
    pageOfUsers: state.getUsers.pageOfUsers,
    status : state.status,
    pageNumber: state.getUsers.pageNumber,
    curPage: state.getUsers.curPage,
    users: state.getUsers.users,
  }
};

const mapDispatchToProps = (dispatch) => {
  return {
    getUsers: () => {
      dispatch(getUsers());
    },
    deleteUser: (_id) => {
      dispatch(deleteUser(_id));
    },
    createUser: (user) => {
      dispatch(createUser(user));
    },
    redirectToUserlist: () => {
      dispatch(redirect());
    },
    resetRedirect: () => {
      dispatch(resetRedirect());
    },
    editUser: (id, user) => {
      dispatch(editUser(id, user));
    },
    sortUsers: (key) => {
      dispatch(sortUsers(key));
    },
    backToHome: () => {
      dispatch(backToHome());
    },
    search: (keyword) => {
      dispatch(search(keyword));
    },
    changeSearchInput: (input) => {
      dispatch(changeSearchInput(input));
    },
    getCount: () => {
      dispatch(getCount());
    }
  }
};
export default connect(mapStateToProps, mapDispatchToProps)(App);
