const initState = {
  isLoading: false,
  users: {},
  err: null
};

const createUser = (state = initState, action) => {
  switch(action.type) {
    case 'CREATE_USER_START':
      return {
        ...state,
        isLoading: true,
        err: null
      };
    case 'CREATE_USER_FAIL':
      return {
        ...state,
        isLoading: false,
        err: action.error
      };
    case 'CREATE_USER_SUCCESS':
      return {
        ...state,
        isLoading: false,
        err: null,
        users: action.data
      };
    default:
      return state;
  }
};

export default createUser
;
