import React, {Component}from "react";
import moment from 'moment';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {usernameInput: '', passwordInput: '', isLogging: false, isPasswordCorrect: true};
  }
  handleUsernameInputChange = e => {
    this.setState({usernameInput: e.target.value});
    console.log('username input: ', e.target.value);
  }

  handlePasswordInputChange = e => {
    this.setState({passwordInput: e.target.value});
    console.log('password input: ', e.target.value);
  }

  handleSubmit = e => {
    e.preventDefault();
    console.log('the text in the form are: ', this.state);

    if ((this.state.usernameInput === 'today' && this.state.passwordInput === moment().format('YYYYMMDD')) || 
        (this.state.usernameInput === 'yesterday' && this.state.passwordInput === moment().add(-1, 'days').format('YYYYMMDD')) ||
        (this.state.usernameInput === 'tomorrow' && this.state.passwordInput === moment().add(1, 'days').format('YYYYMMDD'))) {
          this.setState({isLogging: true});
    } else {
        this.setState({isLogging: false});
        this.setState({isPasswordCorrect: false});
    } 

    this.setState({usernameInput: '', passwordInput: ''});
  }

  handleClick = () => {
    console.log('after logout:', this.state);
    this.setState({usernameInput: '', passwordInput: ''});
  }

  render () {
    const { usernameInput, passwordInput, isLogging, isPasswordCorrect } = this.state;
    return (
      <div className = 'container'>
        <form className = 'inputForm' onSubmit = {this.handleSubmit}>

          <label for = 'username'>Username</label>
          <input type = 'text' value = {usernameInput} onChange = {this.handleUsernameInputChange} />

          <label for = 'password'>Password</label>
          <input type = 'text' value = {passwordInput} onChange = {this.handlePasswordInputChange} />

          <div className = 'loginOrlogout'>

            <div className = 'login'>
              <input type = 'submit' value = 'Login' />
            </div>

            <div className = 'logout'>
              <button onClick = {this.handleSubmit}>Logout</button>
            </div>

          </div>
        </form>

        <div className = 'alert'>{!isPasswordCorrect && 'Username or password is not correct.'}</div>
        <div className = 'alert'>{isLogging && 'Welcome Back'}</div>
        <div className = 'alert'>{!isLogging && 'Please Login'}</div>
      </div>
    );

  }
}

export default App;
